// src/components/LoginPage.js
import React, { useRef, useState, useEffect } from 'react';
import { Container, Button, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import '../assets/css/CustomStyles.css';

const LoginPage = () => {
  const videoRef = useRef(null);
  const [error, setError] = useState(null);
  const [authenticated, setAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const startVideo = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        videoRef.current.srcObject = stream;
      } catch (err) {
        setError('Error accessing webcam');
      }
    };

    startVideo();
  }, []);

  const handleLogin = () => {
    const canvas = document.createElement('canvas');
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;
    const context = canvas.getContext('2d');
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    const imgUrl = canvas.toDataURL('image/png');

    // Send captured image to backend for authentication
    fetch('http://127.0.0.1:5000/verify-user', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ image: imgUrl }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setAuthenticated(true);
          navigate('/dashboard'); // Navigate to Dashboard if authenticated
        } else {
          setError('Face not recognized. Please try again.');
        }
      })
      .catch(() => setError('Authentication failed'));
  };

  return (
    <Container className="login-page mt-5 text-center">
      <h2>Login with Face Recognition</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <div className="video-container">
        <video ref={videoRef} autoPlay muted width="500" height="400" />
      </div>
      <Button variant="primary" className="mt-3" onClick={handleLogin}>
        Login
      </Button>
      {authenticated && (
        <Alert variant="success" className="mt-3">
          Authentication successful! Redirecting to your dashboard...
        </Alert>
      )}
    </Container>
  );
};

export default LoginPage;
