import React, { useEffect, useRef, useState } from 'react';
import { Container, Button, Alert, Image } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import * as faceapi from 'face-api.js';

const FaceCapturePage = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [capturedImage, setCapturedImage] = useState(null); // State to hold the captured face image

  // Initialize face-api.js models
  const loadModels = async () => {
    const MODEL_URL = '/models';  // Path where the models will be served
    try {
      await faceapi.nets.ssdMobilenetv1.loadFromUri(MODEL_URL);
      await faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL);
      await faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL);
      setLoading(false);
    } catch (err) {
      setError('Failed to load face-api models');
      setLoading(false);
    }
  };

  useEffect(() => {
    loadModels();
  }, []);

  useEffect(() => {
    if (loading || error) return;

    // Start video feed from webcam
    const startVideo = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: {} });
        videoRef.current.srcObject = stream;
        videoRef.current.onplay = () => {
          setInterval(() => {
            detectFaces();
          }, 100);
        };
      } catch (err) {
        setError('Error accessing webcam');
      }
    };

    startVideo();
  }, [loading, error]);

  // Detect faces from the video feed
  const detectFaces = async () => {
    if (videoRef.current && canvasRef.current) {
      const detections = await faceapi.detectAllFaces(
        videoRef.current,
        new faceapi.SsdMobilenetv1Options()
      ).withFaceLandmarks().withFaceDescriptors();

      const canvas = canvasRef.current;
      const context = canvas.getContext('2d'); // Get 2D context
      context.clearRect(0, 0, canvas.width, canvas.height); // Clear the canvas

      faceapi.matchDimensions(canvas, videoRef.current);
      const resizedDetections = faceapi.resizeResults(detections, videoRef.current);

      faceapi.draw.drawDetections(canvas, resizedDetections);
      faceapi.draw.drawFaceLandmarks(canvas, resizedDetections);

      // Store the first face detection (if available)
      if (detections.length > 0) {
        const { width, height, x, y } = detections[0].detection.box;
        const imageData = context.getImageData(x, y, width, height);
        setCapturedImage(imageData);  // Store the captured face image data
      }
    }
  };

  // Capture face image and convert it to a displayable format
  const captureFace = () => {
    if (capturedImage) {
      const canvas = document.createElement('canvas');
      canvas.width = capturedImage.width;
      canvas.height = capturedImage.height;
      const context = canvas.getContext('2d');
      context.putImageData(capturedImage, 0, 0);
      const imgUrl = canvas.toDataURL(); // Convert to image URL
      setCapturedImage(imgUrl); // Set the image URL for display
    }
  };

  return (
    <Container className="mt-5 text-center">
      <h1>Face Capture Page</h1>
      <p>Here you will scan your face for attendance.</p>
      {loading && <Alert variant="info">Loading face recognition models...</Alert>}
      {error && <Alert variant="danger">{error}</Alert>}
      <div className="position-relative">
        <video
          ref={videoRef}
          autoPlay
          muted
          width="52zz"
          height="480"
        //   width="640"
        //   height="480"
          style={{ transform: 'scaleX(-1)' }}
        />
        <canvas
          ref={canvasRef}
          width="640"
          height="480"
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            zIndex: 1,
            pointerEvents: 'none',
          }}
        />
      </div>
      <Button variant="primary" className="mt-3" onClick={captureFace}>
        Capture Face
      </Button>
      <div className="mt-3">
        <Link to="/">
          <Button variant="secondary">
            Back to Home
          </Button>
        </Link>
      </div>
      {capturedImage && (
        <div className="mt-3">
          <h4>Captured Face</h4>
          <Image src={capturedImage} fluid />
        </div>
      )}
    </Container>
  );
};

export default FaceCapturePage;
